mapper.select:

select no, name, email, gender
from user
where no = #{no}


select no, name, email, gender
from user
where email = #{email}


select no, name, password, role
from user
where email = #{email}

mapper.update:

update user
set name = #{name}, gender = #{gender}
where no = #{no}


update user
set name = #{name}, password = password(#{password}), gender = #{gender}
where no = #{no}
